import workflow
import clipboard
import re
import console

long_url = workflow.get_parameters()['URL']
if re.match('http(s)?://.*', long_url):
	import urllib
	try:
		short_url = urllib.urlopen('http://is.gd/create.php?format=simple&url=' + urllib.quote(long_url, '')).read()
		if re.match('http://is.gd.*', short_url):
			workflow.set_output(short_url)
		else:
			console.hud_alert('Failed to shorten URL', 'error', 1.5)
	except IOError:
		console.hud_alert('Failed to shorten URL', 'error', 1.5)
else:
	console.hud_alert('Invalid URL', 'error', 1.5)
